import React from "react";
import { Link } from "react-router-dom";
import "./Login.css";
import { contextname } from "./MyContext";
const Login = (props) => {
  const [username, setUsername] = React.useState("");
  const [pass, setPass] = React.useState("");

  const [
    login,
    setLogin,
    usertemp,
    setUserTemp,
    pastemp,
    setPastemp,
    usernamefinal,
    setUsernamefinal,
    passfinal,
    setPassfinal,
  ] = React.useContext(contextname);

  const loginbtn = () => {
    if (username == "" || pass == "") {
      alert("All fields are mandetory");
    } else {
      alert(usertemp+" "+p);
      if (pass == pastemp && username == usertemp) {
        setUsernamefinal(username);
        setPassfinal(pass);
        setLogin(1);
      } else {
        alert("Invalid Credentials");
      }
    }
  };
  return (
    <div>
      <div className="imgcontainer">
        <img alt="" src="logo.png" className="" />
      </div>

      <div className="container">
        <label>
          <b>Username</b>
        </label>
        <input
          onChange={(e) => {
            setUsername(e.target.value);
          }}
          type="text"
          placeholder="Enter Username"
          name="uname"
          required
        />

        <label>
          <b>Password</b>
        </label>
        <input
          onChange={(e) => {
            setPass(e.target.value);
          }}
          type="password"
          placeholder="Enter Password"
          name="psw"
          required
        />

        <button onClick={loginbtn} type="submit">
          Login
        </button>
      </div>

      <div className="container" style={{ backgrounColor: "#f78e21" }}>
        No account?
        <Link to="/signup">
          <button type="button" className="cancelbtn">
            Signup
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Login;
